import React from 'react';


// we have tp say good afternoon and so on according to the time on our machine.
const cssStyle = { };
let curDate = new Date();
curDate = curDate.getHours();
let greet = {};

if (curDate < 12){
    greet = "Good Morning" ;
    cssStyle.color = "green"

}else if (curDate > 12 && curDate < 19){
    greet = "Good Afternoon";
    cssStyle.color = "yellow";

}else {
    greet = "Good Night";
    cssStyle.color = "orange";
}



const App = () => {
    return (
        <h1>
            hello sir,<span>

            {greet}
            </span>
            </h1>
    )
}

export default App;
