import React from 'react';
import ReactDOM from 'react-dom';


const heading = {
    backgroundColor: "yellogreen",
    color: 'red',
    textAlign: 'center',
    alignItems: 'center',
    border: "2px solid black",
    justifyContent: "center"
}
const heading1 = {
    backgroundColor: "blue",
    color: 'red',
    textAlign: 'center',
    alignItems: 'center',
    border: "2px solid black",
    justifyContent: "center"
}
const heading2 = {
    backgroundColor: "darkblue",
    color: 'red',
    textAlign: 'center',
    alignItems: 'center',
    border: "2px solid black",
    justifyContent: "center"
}

const selectBg = () => {
    if (greeting() === afternoon ){
        return (
            heading
        )
    }else if(greeting() === Evening){
        return(
            heading1
        )
    }else {
        heading2
    }
}




const nowDate = new Date();
const nowHours = nowDate.getHours();

const afternoon = "Good Afternoon";
const Evening = "Good Evening";
const Night = "Good Night";

const greeting = () => {
    if (nowHours < 12 ) {
        return (
            <h2 style={{color:"yellowgreen"}}>{afternoon}</h2>
        )       
    }else if (nowHours > 12 && nowHours < 7) {
        return (
            <h2 style={{color:"blueviolet"}}>{Evening}</h2>
        )
    }else {
        return (
            <h2 style={{color:"darkblue"}}>{Night}</h2>
        )
    }
}


ReactDOM.render(
    <>
    <div style={heading}>
    <h1>{greeting()}</h1>
    </div>
    </>
    ,document.getElementById('root'));
